import React from "react";
import ExpensesFilter from "../ExpenseFilter/ExpenseFilter";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import './ExpenseItemList.css'

function ExpenseItemList(props){
    const [filteredYear, setFilteredYear] = React.useState('2020');

    const [filteredExpense , setFilteredExpense] = React.useState(props.expenses);

    const filterChangeHandler = (selectedYear) =>{
        setFilteredYear(selectedYear);
        setFilteredExpense(() => props.expenses.filter(arr => {
            return (arr.date.getFullYear().toString() === selectedYear);}) );
    };

    let expensesContent = <p>No Expenses Found</p>;
    if(filteredExpense.length === 0){
        expensesContent =filteredExpense.map(
            expense => (<ExpenseItem key={expense.id} title= {expense.title} amount = {expense.amount} date = {expense.date} />)
          );
    } 

    return (
        <div>
      <Card className="expenses-item-list"> 
      <ExpensesFilter onSelectOption = {filterChangeHandler} defaultSelected = {filteredYear}/>
    {expensesContent}
     </Card></div>
    );
}

export default ExpenseItemList;
