import React from "react";
import ExpensesFilter from "../ExpenseFilter/ExpenseFilter";
import Card from "../UI/Card";
import ExpenseItem from "./ExpenseItem";
import './ExpenseItemList.css'

function ExpenseItemList(props){
    console.log(props.tems);
    const [filteredYear, setFilteredYear] = React.useState('2020');

    const filterChangeHandler = (selectedYear) =>{
        setFilteredYear(selectedYear);
        console.log(selectedYear);
    };
    return (
        <div>
            <ExpensesFilter onSelectOption = {filterChangeHandler} defaultSelected = {filteredYear}/>
      <Card className="expenses-item-list"> 
      {props.expenses.map(
        expense => (<ExpenseItem title= {expense.title} amount = {expense.amount} date = {expense.date} />)
      )}

        <ExpenseItem title= {props.expenses[1].title} amount = {props.expenses[1].amount} date = {props.expenses[1].date}></ExpenseItem>
        <ExpenseItem title= {props.expenses[2].title} amount = {props.expenses[2].amount} date = {props.expenses[2].date}></ExpenseItem>
        <ExpenseItem title= {props.expenses[3].title} amount = {props.expenses[3].amount} date = {props.expenses[3].date}></ExpenseItem>
     </Card></div>
    );
}

export default ExpenseItemList;
